import React, {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";
const Expenses = (props) => {
    const [selectedYear, setSelectedYear] = useState('2021');
    const [filteredInfoText, setFilteredInfoText] = useState('2019, 2021, 2022 & 2023')
    const filteredYear = (selectedYear) =>{
        setSelectedYear(selectedYear);
        console.log(selectedYear);
    }
    return(
        <Card className="expenses">
            <ExpensesFilter year={selectedYear} onFilterYear={filteredYear}/>
            <p>Data for years {filteredInfoText} is hidden.</p>
            <ExpenseItem
                date={props.items[0].date}
                title={props.items[0].title}
                amount={props.items[0].amount}
                id={props.items[0].id}
            />
            <ExpenseItem
                date={props.items[1].date}
                title={props.items[1].title}
                amount={props.items[1].amount}
                id={props.items[1].id}
            />
            <ExpenseItem
                date={props.items[2].date}
                title={props.items[2].title}
                amount={props.items[2].amount}
                id={props.items[2].id}
            />
            <ExpenseItem
                date={props.items[3].date}
                title={props.items[3].title}
                amount={props.items[3].amount}
                id={props.items[3].id}
            />
        </Card>
    );
}

export default Expenses;
